    work_year = IntegerField(default=0)
    introduction = CharField(max_length=1000, default='', blank=True)
    is_email_verified = BooleanField(default=False)
    location = CharField(max_length=100, default='', blank=True)
    fields = CharField(max_length=100, default='', blank=True)
    skills = CharField(max_length=100, default='', blank=True)
    services = CharField(max_length=100, default='', blank=True)
    views = IntegerField(default=0)
    updated_datetime = DateTimeField(default=now)
    wechat = CharField(max_length=100, default='', blank=True)
    role = IntegerField(default=0, choices=sc.USRE_ROLES)
    job_notification_email = BooleanField(default=True)
    resume_notification_email = BooleanField(default=True)

    profile_facebook = URLField(default='', blank=True)
    profile_google = URLField(default='', blank=True)
    profile_weibo = URLField(default='', blank=True)
    profile_twitter = URLField(default='', blank=True)
    profile_linkedin = URLField(default='', blank=True)

    search_languages = CharField(max_length=1000, default='', blank=True)
    search_edus = CharField(max_length=1000, default='', blank=True)
    search_works = TextField(default='', blank=True)
    search_photos = CharField(max_length=1000, default='', blank=True)

    base64 = TextField(default='data:image/jpg;base64,')
    img_avatar = ImageField(default='', upload_to="img/avatar", blank=True)
